public class JaggedArray{	/*printing jagged array int number1 2 3 3 4 5 	*/ 	public static void main(String args[])	{		int a[][]=new int[3][];	    a[0]=new int[1];	    a[1]=new int[2];		a[2]=new int[3];for (int i=0;i<a.length ;i++ ) {		for (int j=0;j<a[i].length ;j++ ) {			a[i][j]=i+j+1;			System.out.print(a[i][j]+" ");		}			System.out.println("");		}	}}