public class StudentArray{	/*printing array student	*/ 	public static void main(String args[])	{		Student s1=new Student(1,"rameshsds");		Student s2=new Student(1,"rameshas");		Student s3=new Student(1,"ramesha");		Student s4=new Student(1,"rameshs"); 		Student s[]={s1,s2,s3,s4};		for (int i=0;i<4 ;i++ ) {			System.out.println(s[i].roll+":"+s[i].name);		}	}}class Student{		int roll;		String name;Student(int roll,String name){		this.roll=roll;		this.name=name;	}	}